import { React, useState } from 'react';

// Signup Page!
const Signup = () => {
    const [ username, setUsername ] = useState('');
    const [ email, setEmail ] = useState('');
    const [ password1, setPassword1 ] = useState('');
    const [ password2, setPassword2 ] = useState('');
    const [ error, setError ] = useState('');

    const handleUsernameChange = (e) => {
        setUsername(e.target.value);
    };

    const handleEmailChange = (e) => {
        setEmail(e.target.value);
    };

    const handlePassword1Change = (e) => {
        setPassword1(e.target.value);
    };

    const handlePassword2Change = (e) => {
        setPassword2(e.target.value);
    };


    const submitForm = (e) => {
        e.preventDefault();
        const trimmedUsername = username.trim();
        const regex = /^[a-zA-Z0-9]*$/;

        // Handle username not long enough
        if (trimmedUsername.length < 5) {
            setError("Usernames must be five or more characters.")
            return;
        }
        if (regex.test(trimmedUsername)) {
            setError("Usernames must only contain letters and numbers.")
            return;
        }
    }
    return (
        <div className="container">
            <div className="row justify-content-center mt-5">
            <div className="col-md-6">
                <div className="card">
                    <div className="card-body">
                        <h1 className="card-title text-center mb-4">Blog - Signup</h1>
                        <form onSubmit={submitForm}>
                            <div className="mb-3">
                                <label htmlFor="email" className="form-label" >Email</label>
                                <input type="email" className="form-control" id="email" placeholder="Email" onChange={handleEmailChange} required />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="username" className="form-label">Username</label>
                                <input type="text" className="form-control" id="username" placeholder="Username" onChange={handleUsernameChange} required />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="password" className="form-label">Password</label>
                                <input type="password" className="form-control" id="password" placeholder="Password" onChange={handlePassword1Change} required />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="confirmPassword" className="form-label">Confirm Password</label>
                                <input type="password" className="form-control" id="confirmPassword" placeholder="Confirm password" onChange={handlePassword2Change} required />
                            </div>
                            <button type="submit" className="btn btn-primary btn-block">Sign up</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    </div>
    );
};

export default Signup;
