import { useAuthContext } from "../hooks/useAuthContext";
import { Link } from 'react-router-dom';
import { useLocation, useNavigate } from 'react-router';
import { useState } from 'react';

const ProductBuy = ({ product }) => {
    const { user } = useAuthContext();
    const [quantity, setQuantity] = useState(1);
    const navigate = useNavigate();

    // Handle quantity change
    const handleQuantityChange = (value) => {
        // Ensure quantity is within bounds (minimum: 1, maximum: product.quantity_available)
        const newQuantity = Math.min(Math.max(quantity + value, 1), product.quantity_available);
        setQuantity(newQuantity);
    };

    // Handle add to cart
    const handleFormSubmit = (e) => {
        e.preventDefault();
        if (!user) {
            navigate('/login');
            return
        }
    }

    return (
        <div className="col-md-2 justify-content-center align-items-center">
            {product.quantity_available > 0 ? (
                <form onSubmit={handleFormSubmit}> 
                    <div className="input-group w-100 d-flex">
                        <button type="button" className="btn btn-outline-secondary" onClick={() => handleQuantityChange(-1)}>-</button>
                        <input type="text" className="form-control text-center" value={`Quantity: ${quantity}`} readOnly />
                        <button type="button" className="btn btn-outline-secondary" onClick={() => handleQuantityChange(1)}>+</button>
                    </div>
                    <p className="card-text mb-2 text-success">In stock: {product.quantity_available}</p>

                    <button type="submit" className="btn btn-primary w-100 mt-2">Add to Cart</button> 
                </form>
            ) : (
                <p className="card-text mb-2 text-muted">Out of stock</p>
            )}
        </div>
    );
};

export default ProductBuy;
