import { Product } from '../hooks/Product.js';
import { useEffect, useState } from 'react';

const CartEntry = ( {item} ) => {
    const { getProduct, product, isLoading, error } = Product(); // Fetch product details hook
    const [ quantity, setQuantity ] = useState(item.quantity);

    // Fetch product (using API) when page loads
    useEffect(() => {
        getProduct(item.product_id);
    }, []); 

    // Handle quantity change
    const handleQuantityChange = (value) => {
        // Ensure quantity is within bounds (minimum: 1, maximum: product.quantity_available)
        const newQuantity = Math.min(Math.max(quantity + value, 1), product.quantity_available);
        setQuantity(newQuantity);
    };

    // Update quantity
    const handleUpdateOrder = () => {

    }

    return (
        <div className="row border rounded mb-2 ">
            {product && (
                <>
                    <div className="col-md-2 d-flex justify-content-center align-items-center">
                        <img src={product.image_url} className="card-img-left img-fluid" alt={product.name} style={{ height: '250px', backgroundColor: 'white', objectFit: 'contain' }} />
                    </div>
                    <div className="col-md-10 d-flex flex-column align-items-center pt-3">
                        <h5>{product.name}</h5>
                        <div className="input-group quantity-selector">
                            <button type="button" className="btn quantity-button" onClick={() => handleQuantityChange(-1)}>-</button>
                            <input type="text" className="form-control text-center quantity-display bg-white" value={`Quantity: ${quantity}`} readOnly />
                            <button type="button" className="btn quantity-button" onClick={() => handleQuantityChange(1)}>+</button>
                        </div>
                        <div className="row pl-3 pr-3 mb-1 mt-auto">
                            <div className="col-md-6 mr-auto">
                                <p>Price each: ${item.product_price_at_purchase}</p>
                            </div>
                            <div className="col-md-6 ml-auto">
                                <p>Net price: ${quantity * item.product_price_at_purchase}</p>
                            </div>
                        </div>
                    </div>
                </>
            )}
        </div>
    )
}

export default CartEntry;
