const useSignUp = async (username, email, password) => {
     // Request sign up from server
     try {
        const userData = {
            username: username,
            password: password,
            email: email
        };

        const response = await fetch('http://127.0.0.1:3002/signup/request', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(userData)
        });

        if (!response.ok) {
            // If response status is not OK, extract error message from response body
            const errorData = await response.json();
            throw new Error(errorData.errorMessage || 'Unknown server error. Please try again later.');
        }

        const responseData = await response.json();
        const userId = responseData.user_id;

        // Handle successful sign-up
        storeUserInLocalStorage(userId); // Store user ID in local storage

        // Redirect to home page
        navigate('/');
    } catch (error) {
        // If sign up fails, show the user the error message
        setError(error.message);
    }
};

export default useSignUp
