import { FeaturedProducts } from '../hooks/FeaturedProducts.js';
import FeaturedProductCard from '../components/FeaturedProductCard.js';
import React, { useEffect } from 'react';

const Featured = () => {
    const { getFeatured, featured, isLoading, error } = FeaturedProducts();

    useEffect(() => {
        getFeatured();
    }, []);
    return (
<>
    <h2 className="text-center">Featured Products</h2> {/* Title */}

    <div className="row justify-content-center"> {/* Row to contain the product cards */}
        {isLoading ? (
            <div className="col">
                <p>Loading...</p>
            </div>
        ) : error ? (
            <div className="col">
                <p>Error: {error}</p>
            </div>
        ) : featured ? (
            featured.map((product) => (
                <div key={product.id} className="col-6 col-md-4 col-lg-3"> {/* Column classes for product cards */}
                    <FeaturedProductCard product={product} />
                </div>
            ))
        ) : (
            <div className="col">
                <p>No featured products available</p>
            </div>
        )}
    </div>
</>

    )
}

export default Featured;
