import { useAuthContext } from "../hooks/useAuthContext";
import { Link } from 'react-router-dom';
import { useLocation, useNavigate, useState } from 'react-router';

const ProductBuy = ({ product }) => {
    const { user } = useAuthContext();
    const [quantity, setQuantity] = useState(1);

    return (
        <div className="col-md-2 justify-content-center align-items-center">
            <p className="card-text mb-2">Quantity Available: {product.quantity_available}</p>
            {user ? (
                <button className="btn btn-primary">Add to Cart</button>
            ) : (
                <Link to="/login" className="btn btn-primary">Add to cart</Link>
            )}
        </div>
    );
};

export default ProductBuy;
