import { useAuthContext } from "../hooks/useAuthContext";
import { Link } from 'react-router-dom';
import { useLocation, useNavigate } from 'react-router';

const ProductBuy = ({ product }) => {
    const { user } = useAuthContext();
    const history = useNavigate();

    const handleDirectToLoginPage = () => {
        const prevLocation = useLocation();
        history.push(`/login?redirectTo=${prevLocation}`);
    }

    return (
        <div className="col-md-2 justify-content-center align-items-center">
            <p className="card-text mb-2">Quantity Available: {product.quantity_available}</p>
            {user ? (
                <button className="btn btn-primary">Add to Cart</button>
            ) : (
                <button className="btn btn-primary" onClick={handleDirectToLoginPage}>Add to Cart</button>
            )}
        </div>
    );
};

export default ProductBuy;
