import { useState } from 'react';

export const Product = () => {
    const [product, setProduct] = useState(null);
    const [error, setError] = useState(null);
    const [isLoading, setIsLoading] = useState(null);
    const getProduct = async(id) => {
        setIsLoading(true);
        setError(null);

        const response = await fetch(`http://127.0.0.1:3002/products/getOne?id=${id}`, {
        method: 'GET',
        });

        // Handle unsuccessful fetch
        if (!response.ok) {
            const errorData = await response.json();
            setIsLoading(false);
            setError(errorData.errorMessage || 'Unknown server error. Please try again later.');
        }

        // Handle successful fetch
        if (response.ok) {
            console.log(json);
            const json = await response.json();
            setProduct(json);
            setIsLoading(false);
        }
    }

    return { getProduct, product, isLoading, error };
};
