import { Product } from '../hooks/Product.js';
import { useParams } from 'react-router-dom';
import { useEffect } from 'react';

const ProductPage = () => {
    const { getProduct, product, isLoading, error } = Product(); 
    const { productId } = useParams();
    useEffect(() => {
        getProduct(productId);
    }, []); 

    return (
        <div className="container">
        <div className="row">
            <div className="col-md-8 offset-md-2">
                {isLoading && <p>Loading...</p>}
                {error && <p>Error: {error}</p>}
                {product && (
                    <div className="card">
                        <div className="row">
                            <div className="col-md-4">
                                <img src={product.image_url} className="card-img-left" alt={product.name} />
                            </div>
                            <div className="col-md-8">
                                <div className="card-body">
                                    <h1 className="card-title">{product.name}</h1>
                                    <p className="card-text">{product.description}</p>
                                    <p className="card-text">Price: ${product.price}</p>
                                    <p className="card-text">Old Price: ${product.old_price}</p>
                                    <p className="card-text">Quantity Available: {product.quantity_available}</p>
                                    <p className="card-text">Rating: {product.rating}</p>
                                    <p className="card-text">Number of Reviews: {product.num_of_reviews}</p>
                                    <button className="btn btn-primary">Add to Cart</button>
                                </div>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    </div>
    )
};

export default ProductPage;
