import { FeaturedProducts } from '../hooks/FeaturedProducts.js';
import FeaturedProductCard from '../components/FeaturedProductCard.js';
import React, { useEffect } from 'react';

const Featured = () => {
    const { getFeatured, featured, isLoading, error } = FeaturedProducts();

    useEffect(() => {
        getFeatured();
    }, []);
    return (
<div className='col' style={{ padding: 0, margin: 0 }}>
            <h2>Featured Products</h2>
            <div className="row" style={{ margin: 0 }}>
                {isLoading ? (
                    <div className="col">
                        <p>Loading...</p>
                    </div>
                ) : error ? (
                    <div className="col">
                        <p>Error: {error}</p>
                    </div>
                ) : featured ? (
                    featured.map((product) => (
                        <FeaturedProductCard product={product} />
                    ))
                ) : (
                    <div className="col">
                        <p>No featured products available</p>
                    </div>
                )}
            </div>
        </div>

    )
}

export default Featured;
