import { Product } from '../hooks/Product.js';
import { useParams } from 'react-router-dom';
import { useEffect } from 'react';

const ProductPage = () => {
    const { getProduct, product, isLoading, error } = Product(); 
    const { productId } = useParams();
    useEffect(() => {
        getProduct(productId);
    }, []); 

    return (
        <div className="container">
            {isLoading && <p>Loading...</p>}
            {error && <p>Error: {error}</p>}
            {product && (
                <div className="row">
                        <img src={product.image_url} className="card-img" style={ { width: 300, objectFit: 'contain' } } alt={product.name} />
                    <div className="col-md-8">
                        <div className="card-body">
                            <h3 className="card-title">{product.name}</h3>
                            <div className="col-auto d-flex">
                            <div className="star-rating">
                                {[...Array(Math.floor(product.rating))].map((_, index) => (
                                    <span key={index} className="star" style={{ color: 'orange' }}>&#9733;</span>
                                ))}
                                {product.rating % 1 !== 0 && (
                                    <span className="star" style={{ color: 'orange', clipPath: `inset(0 ${100 - product.rating % 1 * 100}% 0 0)` }}>
                                        &#9733;
                                    </span>
                                )}
                            </div>
                            <div className="ml-0">
                                <p className="text-left mb-0">({product.num_of_reviews})</p>
                            </div>
                        </div>
                            <p className="card-text">{product.description}</p>
                            <p className="card-text">Price: ${product.price}</p>
                            <p className="card-text">Old Price: ${product.old_price}</p>
                            <p className="card-text">Quantity Available: {product.quantity_available}</p>
                            <p className="card-text">Rating: {product.rating}</p>
                            <p className="card-text">Number of Reviews: {product.num_of_reviews}</p>
                            <button className="btn btn-primary">Add to Cart</button>
                        </div>
                    </div>
                </div>
            )}
      </div>
    )
};

export default ProductPage;
