const FeaturedProductCard = ( {product} ) => {
    return (
        <div key={product.id} className="col py-3">
            <div className="card h-100 py-3 d-flex">
                <img src={product.image_url} className="card-img-left img-fluid" alt={product.name} style={{ maxHeight: '200px', objectFit: 'contain' }} />
                <div className="card-body d-flex flex-column justify-content-between">
                    <div>
                        <h5 className="card-title">{product.name}</h5>
                    </div>
                    {/* Display star rating and number of reviews */}
                    <div className="row align-items-center">
                    <div className="col-auto star-rating">
    {[...Array(Math.floor(product.rating))].map((_, index) => (
        <span key={index} className="star">&#9733;</span>
    ))}
    {product.rating % 1 !== 0 && (
        <span className="star" style={{ position: 'relative' }}>
            <span style={{ position: 'absolute', overflow: 'hidden', width: '90%' }}>
                &#9733;
            </span>
            <span style={{ position: 'absolute', color: 'transparent' }}>
                &#9733;
            </span>
        </span>
    )}
</div>



                        <div className="col">
                            <p className="text-right mb-0">({product.num_of_reviews})</p>
                        </div>
                    </div>
                    <div className="d-flex align-items-center">
                        <p className="card-text mr-2">${product.price}</p>
                        {product.price < product.old_price && (
                            <p className="card-text text-muted mb-0"><del>${product.old_price}</del></p>
                        )}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default FeaturedProductCard;
