import { useAuthContext } from "../hooks/useAuthContext";
import { Link } from 'react-router-dom';
import { useLocation, useNavigate } from 'react-router';
import { useState } from 'react';

const ProductBuy = ({ product }) => {
    const { user } = useAuthContext();
    const [quantity, setQuantity] = useState(1);
    const navigate = useNavigate();

    const handleFormSubmit = () => {
        if (!user) {
            navigate('/login');
            return
        }
    }

    return (
        <div className="col-md-2 justify-content-center align-items-center">
            <p className="card-text mb-2">Quantity Available: {product.quantity_available}</p>
            <form onSubmit={handleFormSubmit}>
                <div class="quantity-counter">
                    <input type="number" class="quantity-input" value={quantity}></input>
                </div>
            </form>
            <button onClick={handleFormSubmit} className="btn btn-primary">Add to Cart</button>
        </div>
    );
};

export default ProductBuy;
