import { Product } from '../hooks/Product.js';
import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import { RemoveFromCart } from '../hooks/RemoveFromCart.js';

const CartEntry = ( {item} ) => {
    const { getProduct, product, isLoading, error } = Product(); // Fetch product details hook
    const [ quantity, setQuantity ] = useState(item.quantity);
    const { removeFromCart, isSuccess } = RemoveFromCart();
    const [ isDeleted, setIsDeleted ] = useState(false);

    // Fetch product (using API) when page loads
    useEffect(() => {
        getProduct(item.product_id);
    }, []); 

    // Handle quantity change
    const handleQuantityChange = (value) => {
        // Ensure quantity is within bounds (minimum: 1, maximum: product.quantity_available)
        const newQuantity = Math.min(Math.max(quantity + value, 1), product.quantity_available);
        setQuantity(newQuantity);
    };

    // Remove entry from cart
    const removeEntry = async () => {
        await removeFromCart(item.product_id);
        if (isSuccess) {
            setIsDeleted(true);
        }
    }

    return (
        <>
            {product && !isDeleted && (
                <div className="row rounded border mb-2 mx-3">
                    <div className="col-md-1 my-auto">
                        <button className='btn btn-warning btn-sm' onClick={removeEntry}>
                            <FontAwesomeIcon icon={faTrashAlt} /> Remove
                        </button>
                    </div>
                    <div className="image-container mx-auto d-flex justify-content-center align-items-center" style={{ height: '200px' }}>
                        <img 
                            src={product.image_url} 
                            className="img-fluid d-block" 
                            style={{ width: '100%', height: '100%', objectFit: 'contain' }} 
                            alt={product.name} 
                        />
                    </div>
                    <div className="col-md-8 d-flex flex-column r align-items-center pt-3">
                        <Link to={`/product/${product.id}`} style={{ color: 'black', width: '100%', overflow: 'hidden', textOverflow: 'ellipsis', 'white-space': 'nowrap' }}>
                            {product.name}
                        </Link>
                        <div className="row">
                            <div className="input-group quantity-selector my-3">
                                <button type="button" className="btn quantity-button" onClick={() => handleQuantityChange(-1)}>-</button>
                                <input type="text" className="form-control text-center quantity-display bg-white" value={`Quantity: ${quantity}`} readOnly />
                                <button type="button" className="btn quantity-button" onClick={() => handleQuantityChange(1)}>+</button>
                            </div>
                        </div>
                        <div className="container">
                            <div className="row justify-content-between mt-auto pt-3">
                                <p>${product.price} each</p>
                                <p className="font-weight-bold">Net: ${(quantity * product.price).toFixed(2)}</p>
                            </div>
                        </div>
                    </div>
                </div>
        )}
        </>
    )
}

export default CartEntry;
