import { Link } from 'react-router-dom';

const FeaturedProductCard = ( {product} ) => {
    return (
        <div key={product.id} className="col-6 col-md-4 col-lg-2 py-1"> 
            <div className="card h-100 px-1 d-flex"> 
              <img src={product.image_url} className="card-img-left img-fluid" alt={product.name} style={{ maxHeight: '190px', objectFit: 'contain' }} />
                <div className="card-body px-1 py-1 d-flex flex-column justify-content-between">
                    <div className="truncate-text">
                        <Link className="card-title text-decoration-none text-dark" to={`/product/${product.id}`}>{product.name}</Link>
                    </div>
                    {/* Display star rating and number of reviews */}
                    <div className="row align-items-center">
                        <div className="col-auto d-flex align-items-center">
                            <div className="star-rating" style={{ fontSize: '0.4rem' }}>
                                {[...Array(Math.floor(product.rating))].map((_, index) => (
                                    <span key={index} className="star" style={{ color: 'orange' }}>&#9733;</span>
                                ))}
                                {product.rating % 1 !== 0 && (
                                    <span className="star" style={{ color: 'orange', clipPath: `inset(0 ${100 - product.rating % 1 * 100}% 0 0)` }}>
                                        &#9733;
                                    </span>
                                )}
                            </div>
                            <div className="ml-0">
                                <p className="text-left mb-0">({product.num_of_reviews})</p>
                            </div>
                        </div>
                    </div>
                    <div className="d-flex align-items-center">
                        <p className="card-text mr-2" style={{ marginBottom: '0' }}>${product.price}</p>
                        {product.price < product.old_price && (
                            <p className="card-text text-muted mb-0"><del>${product.old_price}</del></p>
                        )}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default FeaturedProductCard;
