import { FeaturedProducts } from '../hooks/FeaturedProducts.js';
import FeaturedProductCard from '../components/FeaturedProductCard.js';
import React, { useEffect } from 'react';

const Featured = () => {
    const { getFeatured, featured, isLoading, error } = FeaturedProducts();

    useEffect(() => {
        getFeatured();
    }, []);
    return (
        <div>
        <h2 className="text-center mb-3">Featured Products</h2> {/* Title */}
        <div className='row'>

            {isLoading ? (
                <div className="col">
                    <p>Loading...</p>
                </div>
            ) : error ? (
                <div className="col">
                    <p>Error: {error}</p>
                </div>
            ) : featured ? (
                featured.map((product) => (
                    <FeaturedProductCard key={product.id} product={product} />
                ))
            ) : (
                <div className="col">
                    <p>No featured products available</p>
                </div>
            )}
            </div>
            </div>
    )
}

export default Featured;
