import { useState } from 'react';
import { useAuthContext } from './useAuthContext';

const useSignUp = (username, email, password) => {
    const [error, setError] = useState(null);
    const [isLoading, setIsLoading] = useState(null);

    const signup = async(username, email, password) => {
        setIsLoading(true);
        setError(null);

        const userData = {
            username: username,
            password: password,
            email: email
        };

        const response = await fetch('http://127.0.0.1:3002/signup/request', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(userData)
        });

        if (!response.ok) {
            // If response status is not OK, extract error message from response body
            const errorData = await response.json();
            setIsLoading(false);
            setError(errorData.errorMessage || 'Unknown server error. Please try again later.');
            return;
        }

        // Handle successful sign-up
        const responseData = await response.json();
        const userId = responseData.user_id;
        const token = responseData.token;
        localStorage.setItem('userId', userId);
        setIsLoading(false);


        // Redirect to home page
        navigate('/');

    }
};

export default useSignUp
