import { Product } from '../hooks/Product.js';
import { useParams } from 'react-router-dom';
import { useEffect } from 'react';

const ProductPage = () => {
    const { getProduct, product, isLoading, error } = Product(); 
    const { productId } = useParams();
    useEffect(() => {
        getProduct(productId);
    }, []); 

    return (
        <div className="container">
        <div className="row">
            <div className="col">
                {isLoading && <p>Loading...</p>}
                {error && <p>Error: {error}</p>}
                {product && (
                    <div>
                        <h1>{product.name}</h1>
                        <p>Description: {product.description}</p>
                        <p>Price: ${product.price}</p>
                        <p>Old Price: ${product.old_price}</p>
                        <p>Quantity Available: {product.quantity_available}</p>
                        <img src={product.image_url} alt={product.name} style={{ maxWidth: '100%' }} />
                        <p>Created At: {product.created_at}</p>
                        <p>Updated At: {product.updated_at}</p>
                        <p>Active: {product.active ? 'Yes' : 'No'}</p>
                        <p>Featured: {product.featured ? 'Yes' : 'No'}</p>
                        <p>Category ID: {product.category_id}</p>
                        <p>Rating: {product.rating}</p>
                        <p>Amount Purchased: {product.amount_purchased}</p>
                        <p>Number of Reviews: {product.num_of_reviews}</p>
                    </div>
                )}
            </div>
        </div>
    </div>
    )
};

export default ProductPage;
