import { AuthContext } from "../AuthContext";
import { useContext } from 'react';

export const useAuthContext = () => {
    const context = useContext(AuthContext);

    if (!context) {
        throw Error("useAuthContext must be used inside an AuthContext");
    }

    return context;
}
